mapper.update:
		UPDATE tbl_product       
           SET prodname = #{prodname} 
             , price    = #{price} 
             , quantity = #{quantity} 
             , moddate  = sysdate 
         WHERE prodcod = #{prodcod}

mapper.select:
SELECT * FROM (SELECT rownum rnum, prodcod, prodname, price, quantity, regdate, moddate FROM tbl_product WHERE prodcod > 0
ORDER BY prodcod DESC
) WHERE (rnum >= #{pageStart} AND rnum <= #{pageEnd})
